    @media screen and (max-width: 900px) {
        top: auto;
        bottom: 3%;
        right: auto;
        grid-template-columns: repeat(4, 1fr);
        width: 100%;
        grid-gap: 0em;
    }
    .headerItemDiv {
        border-radius: 50%;
        height: 4em;
        width: 4em;
        border: 4px solid var(--dark-background-color);
