/* Hero --Section */
.heroSection {
    width: 100%;
    height: 100vh;
    z-index: 1;
    overflow: hidden;
    position: relative;
    margin: 0 auto;

    // key line for the parallax effect
    transform-style: preserve-3d;
    z-index: -1;
    img {
        position: absolute;
        width: 100%;
        bottom: 0;
        object-fit: cover;
    }

    .heroSectionTextDiv {
        width: 100%;
        height: 100%;
        z-index: 5;
        margin: 0 auto;
        display: flex;
        flex-direction: column;
        justify-content: center;
        position: absolute;
        padding-left: 4em;
        .heroSectionPreTextEl {
