            width: 100%;
         }
        .heroSectionMainTextEl {
            width: 100%;
            text-align: left;
            color: var(--accent-text-color);
            text-shadow: 2px 2px 3px var(--dark-text-color);
            margin: 0.2em auto;
            font-size: 3.5em;
            padding-left: 0;
        }
        .heroSectionPostText {
            margin-top: 0.5em;
        }
        .heroSectionCTAlinkDiv {
            width: 100%;
            display: block;
            padding-left: 0.2em;
            .heroSectionCTAlink {
                width: 150px;
                text-decoration: none;
                padding: 0.8em 0.8em;
                background: var(--more-transparent-bg-color);
                color: var(--dark-text-color);
                font-weight: bolder;
                cursor: pointer;
                border-radius: var(--border-radius);
                border: solid 2px var(--dark-background-color);
                transition: var(--transition-duration);
                &:hover {
                    background: var(--less-transparent-bg-color);
                    border: solid 2px var(--less-transparent-bg-color);
                }
            }
        }
