.imagesAndTextSection {
    width: 100%;
    height: 100vh;
    margin: 0;
    padding: 0;
    box-shadow: 0px 0px 20px 20px var(--dark-background-color);
    .imagesWithTextDivEachItem {
        width: fit-content;
        height: fit-content;
        display: flex;
        background-color: var(--dark-background-color);
        @media screen and (max-width: 900px) {
            
            & {
                display: block;
            }
            .imagesWithTextImgDiv {
                padding-top: 2em;
            }
        }
        .imagesWithTextImgDiv {
            width: 50%;
            height: auto;
            @media screen and (max-width: 900px) {
                & {
                    width: 80%;
                }
            }
            }
            .imagesWithTextImgEl {
                height: auto;
                width: 100%;
            }
        }
        .imagesWithTextTextDiv {
            margin: 0;
            padding: 0;
            padding-left: 3em;
            width: 50%;
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            text-align: left;
            background-color: var(--dark-background-color);
            h1, p {
                color: var(--light-text-color);
                text-align: left;
            }
            .imagesWithTextTextHeader {
                width: 100%;
                padding-bottom: 1em;
            }
            .imagesWithTextTextPar {
                width: 100%;
                padding-right: 25%;
            }
            @media screen and (max-width: 900px) {
                & {
                    padding-left: 0em;
                    width: 100%;
                    text-align: center;
                }
                h1, p {
                    text-align: center;
                }
                .imagesWithTextTextHeader {
                    padding-bottom: 0em;
                }
                .imagesWithTextTextPar {
                    padding: 1em 4em;
                }
        }
    }
}
