    }
    .videoTextOverlaySVG {
        position: absolute;
        z-index: 1;
        transform: translateY(-3em);
        @media only screen and (max-width: 600px) {
            transform: translateY(-1em);
        }
    }
    .videoWithTextOverlayVideoDiv {
        width: 100%;
        height: 100%;
        position: relative;
        @media only screen and (max-width: 900px) {
            width: auto;
            height: 110%;
        }
        @media only screen and (max-width: 570px) {
            width: auto;
            height: 130%;
        }
        .videoWithTextOverlayVideoEl {
            position: absolute;
            width: 100%;
            height: 100%;
            object-fit: cover;
            right: 0;
            filter: brightness(15%);
            @media only screen and (max-width: 900px) {
                width: auto;
                height: 100%;
            }
            .videoWithTextOverlayVideoSrc {
                width: 100%;
                height: 100%;
            }
        }
    }
    .videoWithTextOverlayTextDiv {
        z-index: 5;
        width: 100%;
        height: auto;
        top: 20%;
        position: absolute;
        display: grid;
        justify-items: center;
        @media only screen and (max-width: 900px) {
            top: 10%;
        }
        h2, p {
            color: var(--light-text-color);
            z-index: 5;
            max-width: 71%;
            text-align: center;
            margin-top: 4%;
            text-shadow: 2px 2px 4px var(--accent-text-color);
            font-size: 1.5em;
            line-height: 2em;
        }
        .videoWithTextOverlayHeadingEl {
            font-size: 3em;
            position: relative;
        }
        .videoWithTextOverlayHeadingEl:after {
            position: absolute;
            content: '';
            width: 100%;
            height: 2px;
            bottom: 0;
            left: 0;
            background-color: var(--accent-bg-color);
        }
        .videoWithTextOverlaySubTextEl {
           padding-bottom: 2.2em; 
        }
        .videoWithTextOverlayButtonDiv {
            display: grid;
            grid-gap: 1em;
            grid-template-columns: repeat(4, 1fr);
            width: 100%;
            justify-items: center;
            max-width: 70%;
            @media screen and (max-width: 1000px) {
                grid-template-columns: repeat(2, 1fr);
                max-width: 90%;
            }
            .videoWithTextOverlayButtonEl {
                text-decoration: none;
                .videoWithTextOverlayButton {
                    width: 150px;
                    text-decoration: none;
                    padding: 1em 2em;
                    background: var(--more-transparent-bg-color);
                    color: var(--light-text-color);
                    font-weight: bolder;
                    cursor: pointer;
                    border-radius: var(--border-radius);
                    border: solid 2px var(--light-background-color);
                    transition: var(--transition-duration);
                    &:hover {
                        background: var(--less-transparent-bg-color);
                        border: solid 2px var(--less-transparent-bg-color);
                    }
                }
            }
        }
    }
}
